function setTreeData(source) {
    let cloneData = JSON.parse(JSON.stringify(source))
    // 对源数据深度克隆
    return cloneData.filter(father=>{
        // 循环所有项，并添加children属性
        let branchArr = cloneData.filter(child=>father.id == child.parentId);
        // 返回每一项的子级数组
