function getNestedChildren(arr, parent) {
    var out = []
    for (let i = 0; i < arr.length; i++) {
        if (arr[i].parent == parent) {
            var children = getNestedChildren(arr, arr[i].id)
            if (children.length) {
                arr[i].children = children
            }
            out.push(arr[i])
        }
    }
    return out
}

