/**
 * 比较两个对象是否相等
 * 相等返回obj2
 * 不等 返回不相同的值 和 reserveArray数组保留的值
 *
 * @export
 * @param {*} obj1
 * @param {*} obj2
 * @param {*} reserveArray
 * @returns
 */
export function getObjectDifferent(obj1, obj2, reserveArray) {
  var data = {}
  var isEqually = true
  for (var key in obj1) {
    if (obj1.hasOwnProperty(key) && obj1[key] !== obj2[key]) {
      data[key] = obj2[key]
      isEqually = false
    }
  }

  if (isEqually) {
    data = obj2
  } else {
    for (var i = 0; i < reserveArray.length; i++) {
      var item = reserveArray[i]
      data[item] = obj2[item]
    }
  }
