function getDate() {
    var today = new Date();
    var time = twoDigits(today.getHours()) + ":" + twoDigits(today.getMinutes());
    $(".date_time").html(time);
}

function twoDigits(val) {
    if (val < 10) return "0" + val;
    return val;
}
getDate();

//æ¯ç§’è·å–æ‰§è¡Œä¸€æ¬¡ï¼Œæ›´æ–°æ—¶é—´
