        { a: '111', b: '2', c: '3' },
        { a: '111', b: '2', c: '3' },
        { a: '2222', b: '1', c: '2' },
        { a: '2222', b: '1', c: '2' },
        { a: '111', b: '2222', c: '3333' },
        { a: '111', b: '2222', c: '3333' }
      ],
      spanArr: []
    }
  },
  methods: {
    // groupBy æ•°ç»„
    groupBy (data, params) {
      const groups = {}
      data.forEach(v => {
        const group = JSON.stringify(v[params])
        groups[group] = groups[group] || []
        groups[group].push(v)
      })
      return Object.values(groups)
    },
    // è®¡ç®— æ•°æ®åˆå¹¶ ç´¢å¼•
    getSpanArr (data, params) {
      // æ¥æ”¶é‡æ„æ•°ç»„
      let arr = []

      // è®¾ç½®ç´¢å¼•
      let pos = 0

      // æ§åˆ¶åˆå¹¶çš„æ•°ç»„
      this.spanArr = []

      // arr å¤„ç†
      this.groupBy(data, params).map(v => (arr = arr.concat(v)))
      
      // this.tableData = arr
      arr.map(res => {
        data.shift()
        data.push(res)
      })

      // spanArr å¤„ç†
      const redata = arr.map(v => v[params])
      redata.reduce((old, cur, i) => {
        if (i === 0) {
          this.spanArr.push(1)
          pos = 0
        } else {
          if (cur === old) {
            this.spanArr[pos] += 1
            this.spanArr.push(0)
          } else {
            this.spanArr.push(1)
            pos = i
          }
        }
        return cur
      }, {})
    },

    // åˆå¹¶ tableData æ•°æ®
    cellMerge ({ row, column, rowIndex, columnIndex }) {
      if (columnIndex === 0) {
        const _row = this.spanArr[rowIndex]
        const _col = _row > 0 ? 1 : 0
        return {
          rowspan: _row,
          colspan: _col
        }
      }
    }
  },
  created () {
    this.getSpanArr(this.tableData, 'a')
  }
}
</script>

