import router from "@/router/index";

/**
 * èœå•æ•°ç»„è½¬æ ‘
 * @param list
 * @returns {[]}
 */
export const listToTree = list => {
  list.sort((a, b) => {
    return a.sort - b.sort
  })
  const map = {}
  let node
  const tree = []

  for (let i = 0; i < list.length; i++) {
    map[list[i].name] = i;
  }

  for (let i = 0; i < list.length; i++) {
    node = list[i];
    if (node.parent) {
      const children = list[map[node.parent]].children || []
      list[map[node.parent]].children = [...children, node]
    } else {
      tree.push(node)
    }
  }
  return tree
}

/**
 * è·Ÿæ®åç«¯è¿”å›çš„æƒé™è·¯ç”± æ ‘ ç”Ÿæˆvue-routeræ‰€éœ€è¦çš„è·¯ç”±æ ‘
 * æ³¨: componentè¿™é‡Œä¸åŠ è½½ã€‚å› ä¸ºç”Ÿæˆçš„è·¯ç”±æ ‘ï¼Œé™¤äº†èœå•ä½¿ç”¨å¤–, è¿˜éœ€è¦åœ¨vue-routerä¸­åšä¸ºåŠ¨æ€è·¯ç”±ä½¿ç”¨
 * @param menuList
 * @returns {*}
 */
export const generatorUserMenuForTree = (menuList) => {
  const userRoutes = menuList.map(menu => {
    const {parent, icon, name, children = [], path, hidden = false, title} = menu
    const i18nTitle = parent ? `${parent}.${name}` : `${name}` // nameä¸ä¸€å®šä¸ºä¸­æ–‡ï¼Œå¦‚æœç”¨åˆ°i18nï¼Œåˆ™å¯ä»¥è·Ÿæ®nameå»åš
    // keyæ˜¯å”¯ä¸€çš„ï¼Œé˜²æ­¢é‡å¤ï¼Œæ‰€ä»¥æ‹¼ä¸Šçˆ¶çº§èœå•
    const key = parent ? `${parent}-${name}` : `${name}`
    // todo: å¦‚æœåç«¯è¿”å›çš„çˆ¶çº§èœå•çš„è·¯å¾„ä¸ºä¸å¸¦'/'å·ï¼Œåˆ™éœ€è¦æ‹¼ä¸Š '/', å­èœå•ä¸éœ€è¦ï¼Œå› ä¸ºvue-routerä¼šè‡ªåŠ¨è·Ÿæ®è·¯å¾„æ‹¼
    const currentMenu = {
      path,
      name,
      hidden,
      parent,
      meta: {
        key: key,
        title,
        i18nTitle,
        icon
      },
      children: children.length === 0 ? [] : generatorUserMenuForTree(children)
    }
    if (children.length <= 0) {
      delete currentMenu.children
    }
    return currentMenu
  })
  return userRoutes
}

/**
 * è·Ÿæ®åç«¯è¿”å›çš„æƒé™è·¯ç”± æ•°ç»„ ç”Ÿæˆvue-routeræ‰€éœ€è¦çš„è·¯ç”±æ ‘
 * @param menuList
 * @returns {*}
 */
export const generatorUserMenuForList = menuList => {
  // å¦‚æœåç«¯è¿”å›çš„æ˜¯çº¯æ•°ç»„çš„èœå•ï¼ˆå³ï¼šæ²¡æœ‰è½¬æ¢æˆèœå•æ ‘ç»“æ„çš„ï¼Œè¦å…ˆè½¬åŒ–æˆæ ‘ç»“æ„ï¼‰
  const tree = listToTree(menuList)
  const routes = generatorUserMenuForTree(tree)
  // æœ€åæ·»åŠ 404é¡µé¢
  routes.push({
    path: '/:pathMatch(.*)*',
    hidden: true,
    redirect: '/error/404'
  })
  return routes
}

/**
 * ä¸ºæ‰€æœ‰è·¯ç”±æ·»åŠ componentè§†å›¾
 * @param routes
 */
export const setUserRouteComponent = routes => {
  routes.forEach(r => {
    r.component = !r.parent ? permissionRoutes.Layout : permissionRoutes[r.name]
    if (r.children && r.children.length > 0) {
      setUserRouteComponent(r.children)
    }
  })
}

/**
 * ç”¨æˆ·æƒé™è·¯ç”±ä¸­æ˜¯å¦åŒ…å«å½“å‰è®¿é—®è·¯ç”±çš„è·¯å¾„ï¼Œå¦‚æœ‰åˆ™å¯ä»¥æ·»åŠ 
 * @param routes
 * @param path
 * @returns {boolean}
 */
export const findRouteForUserRoutes = (routes, path) => {
  let hasRoute = false
  const routeArr = path.split('/')
  const routePath = routeArr[routeArr.length - 1]
  for (let i = 0; i < routes.length; i++) {
    const { path, children = [] } = routes[i]
    if (path === routePath) {
      hasRoute = true
    } else if (children.length > 0) {
      hasRoute = findRouteForUserRoutes(children, routePath)
    }
    if (hasRoute) {
      break
    }
  }
  return hasRoute
}

